<script>
$(function(){
  $('#scrollTopBtn').hide();
  $(window).scroll(function(){
    $(this).scrollTop() > 400 ? $('#scrollTopBtn').fadeIn() : $('#scrollTopBtn').fadeOut();
  });
  $('#scrollTopBtn').click(function(){
    $('html, body').animate({scrollTop:0}, 0);
  });
});
</script>

